using System; using System.Threading.Tasks;  namespace SingletonDemo {     class Program     {         static void Main(string[] args)         {             Parallel.Invoke(                 () => PrintStudentDetails(),                 () => PrintEmployeeDetails()             );           }          private static void PrintEmployeeDetails()         {             Singleton fromEmployee = Singleton.GetInstance;             fromEmployee.PrintDetails("From Employee");         }          private static void PrintStudentDetails()         {             Singleton fromStudent = Singleton.GetInstance;             fromStudent.PrintDetails("From Student");         }     } }  