using System; namespace SingletonDemo {     public sealed class Singleton     {         private static int counter = 0;         private static readonly Lazy<Singleton> instance = new Lazy<Singleton>(()=>new Singleton());          public Singleton()         {             counter++;             PrintDetails("Counter value" + counter.ToString());         }          public static Singleton GetInstance         {             get => instance.Value;         }          public void PrintDetails(String message)         {             Console.WriteLine(message);         }     } }  