using System; namespace SingletonDemo {     public sealed class Singleton     {         private static int counter = 0;         private static readonly Singleton instance = new Singleton();          public Singleton()         {             counter++;             PrintDetails("Counter value" + counter.ToString());         }          public static Singleton GetInstance         {             get             {                 return instance;             }         }          public void PrintDetails(String message)         {             Console.WriteLine(message);         }     } }  