using System; namespace SingletonDemo {     public sealed class Singleton     {         private static int counter = 0;          private static Singleton instance = null;          public Singleton()         {             counter++;             PrintDetails("Counter value" + counter.ToString());         }          public static Singleton GetInstance         {             get             {                 if (instance == null)                     instance = new Singleton();                 return instance;             }         }          public void PrintDetails(String message)         {             Console.WriteLine(message);         }     } }  